definition.script:
pipeline 
{
    agent 
    {
        label 'docker-node'
    }
    stages 
    {
        stage('Clone') 
        {
            steps 
            {
                git branch: 'main', url: 'http://192.168.99.202:3000/exam/exam'
            }
        }
        stage('Prepare')
        {
            steps
            {
                sh 'docker network ls | grep exam-net || docker network create exam-net'
            }
        }
        stage('Build')
        {
            steps
            {
                sh 'cd /home/vagrant/workspace/Exam/client && docker image build -t img-cons .'
                sh 'cd /home/vagrant/workspace/Exam/generator && docker image build -t img-prod .'
                sh 'cd /home/vagrant/workspace/Exam/storage && docker image build -t img-stor .'
            }
        }
         stage('Deploy')
        {
            steps
            {
                sh 'docker container rm -f con-storage || true'
                sh 'docker container run -d --name con-storage -e MYSQL_ROOT_PASSWORD=Exam-2021 --net exam-net img-stor'
                sh 'docker container rm -f con-generator || true'
                sh 'docker container run -d --name con-generator --net exam-net img-prod'
                sh 'docker container rm -f con-client || true'
                sh 'docker container run -d --name con-client --net exam-net -p 8080:5000 img-cons'
            }
        }
    }
}
